  onFileListChange(updatedFileList: any[]): void { 
    const uploadFileArray = this.form.get('uploadFileList') as FormArray;
    uploadFileArray.clear();
    updatedFileList.forEach((file) => {
      const fileGroup = new FormGroup({
        tedEccDocDto: new FormControl(file.tedEccDocDto),
        file: new FormControl(file.file)
      });
      uploadFileArray.push(fileGroup);
    });    
}

//AWAIT decision on custom components for input fields
  getFormControlErrors(controlName: string): string[] {
    const control = this.form.get(controlName);
    const errors: string[] = [];
    if (control && control.errors) {
      for (const error in control.errors) {
        switch (error) {
          case 'required':
            errors.push('NOT_EMPTY');
            break;
          default:
            errors.push('This field has an error.');
        }
      }
    }
    return errors; 
  }

